import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { NotificationBannerContext } from "../NotificationBanner/NotificationContext";
import "./index.css";

const ProductCard = ({ data }) => {
    const { setNotifyMessage } = useContext(NotificationBannerContext);

    const isProductInCart = (productId) => {
        const cart = JSON.parse(localStorage.getItem("cart"));
        if (cart) {
            return cart.some((item) => item === productId);
        }
        return false;
    };

    const isProductInCartFlag = isProductInCart(data._id);
    const addToCardBgColor = isProductInCartFlag ? "bg-green-50" : "bg-white";

    const addToCart = () => {
        if (!isProductInCartFlag) {
            if (localStorage.getItem("cart")) {
                const cart = JSON.parse(localStorage.getItem("cart"));
                cart.push(data._id);
                localStorage.setItem("cart", JSON.stringify(cart));
            } else {
                localStorage.setItem("cart", JSON.stringify([data._id]));
            }
            setNotifyMessage("Added to cart", "success", 1000);
        }
    };
    // console.log(data.description);

    return (
        <div className="product-card m-1 bg-white w-72 p-6 flex flex-col">
        <div className="max-w-sm w-full lg:max-w-full lg:flex">
  <div className="h-48 lg:h-auto lg:w-48 flex-none bg-cover rounded-t lg:rounded-t-none lg:rounded-l text-center overflow-hidden" style="background-image: url('/img/card-left.jpg')" title="Woman holding a mug">
  </div>
  <div className="border-r border-b border-l border-gray-400 lg:border-l-0 lg:border-t lg:border-gray-400 bg-white rounded-b lg:rounded-b-none lg:rounded-r p-4 flex flex-col justify-between leading-normal">
    <div className="mb-8">
      <p className="text-sm text-gray-600 flex items-center">
        <svg className="fill-current text-gray-500 w-3 h-3 mr-2" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
          <path d="M4 8V6a6 6 0 1 1 12 0v2h1a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2v-8c0-1.1.9-2 2-2h1zm5 6.73V17h2v-2.27a2 2 0 1 0-2 0zM7 6v2h6V6a3 3 0 0 0-6 0z" />
        </svg>
        Members only
      </p>
      <div className="text-gray-900 font-bold text-xl mb-2">Can coffee make you a better developer?</div>
      <p className="text-gray-700 text-base">Lorem ipsum dolor sit amet, consectetur adipisicing elit. Voluptatibus quia, nulla! Maiores et perferendis eaque, exercitationem praesentium nihil.</p>
    </div>
    <div className="flex items-center">
      {/* <img className="w-10 h-10 rounded-full mr-4" src="/img/jonathan.jpg" alt="Avatar of Jonathan Reinink"/> */}
      <div className="text-sm">
        <p className="text-gray-900 leading-none">Jonathan Reinink</p>
        <p className="text-gray-600">Aug 18</p>
      </div>
    </div>
  </div>
</div>
            <Link to={`/product-details/${data._id}`}>
                <a>
                    <img
                        className="hover:grow hover:shadow-lg p-2"
                        src={data.image}
                    />
                    <div className="py-3 text-base flex items-center justify-between">
                        <p className>{data.name}</p>
                    </div>

                    <div className="flex">
                        <p className="pt-1 text-gray-900 font-bold text-lg">
                            ₹ {data.price}
                        </p>
                        <p>
                            <span className="ml-2 text-red-500 line-through text-base">
                                {data.price + data.price / 10}
                            </span>
                        </p>
                    </div>
                </a>
            </Link>
            <button
                onClick={addToCart}
                className={`w-full flex justify-center my-4 ${addToCardBgColor} hover:bg-gray-100 border-2 border-green-300 h-6 text-green-300 flex items-center py-2 px-4 rounded`}
            >
                {isProductInCartFlag ? <>Added</> : <>Add to Cart</>}
            </button>
        </div>
    );
};

export default ProductCard;
