import React, { useEffect, useState } from "react";
import { v4 as uuidv4 } from "uuid";

const OrderConfirmed = () => {
    const [orderId, setOrderId] = useState(uuidv4());

    useEffect(() => {
        const order = localStorage.getItem("order");
        const prevOrder = JSON.parse(localStorage.getItem("prevOrder"));
        if (order) {
            if (prevOrder) {
                localStorage.setItem(
                    "prevOrder",
                    JSON.stringify([...prevOrder, { id: orderId, order }])
                );
            } else {
                localStorage.setItem(
                    "prevOrder",
                    JSON.stringify([{ id: orderId, order }])
                );
            }
        }
        localStorage.removeItem("order");
    }, []);

    return (
        <div className="order-confirmed w-full flex items-center mt-16 text-xl justify-center flex-col">
            <h1>Thank you for your order!</h1>
            <p>Your order has been placed successfully.</p>
            <p>You will receive an email confirmation shortly.</p>
            <p>
                Your order id <span className="font-bold">{orderId}</span>
            </p>
        </div>
    );
};

export default OrderConfirmed;
