import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { NotificationBannerContext } from "../NotificationBanner/NotificationContext";
import "./index.css";

const ProductCard = ({ data }) => {
  const { setNotifyMessage } = useContext(NotificationBannerContext);

  const isProductInCart = (productId) => {
    const cart = JSON.parse(localStorage.getItem("cart"));
    if (cart) {
      return cart.some((item) => item === productId);
    }
    return false;
  };

  const isProductInCartFlag = isProductInCart(data._id);
  const addToCardBgColor = isProductInCartFlag ? "bg-green-50" : "bg-white";

  const addToCart = () => {
    if (!isProductInCartFlag) {
      if (localStorage.getItem("cart")) {
        const cart = JSON.parse(localStorage.getItem("cart"));
        cart.push(data._id);
        localStorage.setItem("cart", JSON.stringify(cart));
      } else {
        localStorage.setItem("cart", JSON.stringify([data._id]));
      }
      setNotifyMessage("Added to cart", "success", 1000);
    }
  };



  return (
    <div className="flex flex-row">
      <div className="product-card m-1 bg-white w-72 p-6 flex flex-col">
        <Link to={`/product-details/${data._id}`}>
          <a>
          <div className="max-w-md w-full lg:flex-col justify-start">
            <img className="hover:grow hover:shadow-lg" src={data.image} />
            <div className="border-r border-b border-l border-grey-light lg:border-l-0 lg:border-t lg:border-grey-light bg-white rounded-b lg-rounded-b-none lg-rounded-r p-4 flex flex-col justify-between leading-normal">
              <div className="mb-8">
                <p className="text-sm text-grey-dark flex items-center">
                  <svg
                    className="text-grey w-3 h-3 mr-2"
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 20 20"
                  >
                    <path d="M4 8V6a6 6 0 1 1 12 0v2h1a2 2 0 0 1 2 2v8a2 2 0 0 1-2 2H3a2 2 0 0 1-2-2v-8c0-1.1.9-2 2-2h1zm5 6.73V17h2v-2.27a2 2 0 1 0-2 0zM7 6v2h6V6a3 3 0 0 0-6 0z" />
                  </svg>
                  Appoinment
                </p>
                <div className="text-black font-bold text-xl mb-2">
                 Appoinment Info
                </div>
                <p className="text-grey-darker text-base">{data.description.substring(0,100)+" " + "Read More.." }</p>
              </div>
              <div className="flex items-center">
                <img
                  className="w-10 h-10 rounded-full mr-4"
                  src="https://pbs.twimg.com/profile_images/885868801232961537/b1F6H4KC_400x400.jpg"
                  alt="Avatar of Jonathan Reinink"
                />
                <div className="text-sm">
                  <p className="text-black leading-none">{data.name}</p>
                  <p className="text-grey-dark">Aug 18</p>
                </div>
              </div>
            </div>
          </div>
          </a>
        </Link>
         <button
                onClick={deteteAppoinment}
                className={`w-full flex justify-center  ${addToCardBgColor} hover:bg-gray-100 border-2 border-red-300 h-6 text-red-300 flex items-center py-2 px-4 rounded`}
            >
                Delete
            </button>
        <button
          onClick={addToCart}
          className={`w-full flex justify-center my-4 ${addToCardBgColor} hover:bg-gray-100 border-2 border-green-300 h-6 text-green-300 flex items-center py-2 px-4 rounded`}
        >

          {isProductInCartFlag ? <>Added</> : <>Add to Cart</>}
        </button>
      </div>
    </div>
  );
};

export default ProductCard;
