import React, { useContext, useEffect, useState } from "react";
import { useHistory } from "react-router";
import { api } from "../../api";
import { NotificationBannerContext } from "../NotificationBanner/NotificationContext";

const ProductInfo = ({ id, setTotalAmount, setOrder }) => {
    const { setNotifyMessage } = useContext(NotificationBannerContext);
    const [productData, setProductData] = useState();
    const [quanity, setQuanity] = useState(1);

    useEffect(() => {
        api.getProductById(id).then((res) => {
            setProductData(res);
            setTotalAmount((value) => value + res.price * quanity);
            setOrder((value) => {
                return { ...value, [res._id]: "1" };
            });
        });
    }, [id]);

    const changeQuanity = (e) => {
        setTotalAmount((value) => value - productData.price * quanity);
        setQuanity(e.target.value);
        setTotalAmount((value) => value + productData.price * e.target.value);
        setOrder((value) => {
            return { ...value, [productData._id]: e.target.value };
        });
    };

    const removeProductFromCart = () => {
        const cart = JSON.parse(localStorage.getItem("cart"));
        cart.pop(productData._id);
        localStorage.setItem("cart", JSON.stringify(cart));
        setNotifyMessage("Removed to cart", "error", 1000);
    };

    const removeProduct = () => {
        removeProductFromCart(productData._id);
        setTotalAmount((value) => value - productData.price * quanity);
        setProductData(null);
    };

    return (
        <>
            {productData && (
                <>
                    <div className="product-info m-2 p-2 items-center w-2/3 flex h-12 bg-gray-100 ">
                        <div className="w-3/6">{productData.name}</div>
                        <div className="w-1/6">₹ {productData.price}</div>
                        <div className="product-quantity w-1/6 flex items-center my-4">
                            Quantity:
                            <select onChange={changeQuanity} className="ml-2">
                                <option>1</option>
                                <option>2</option>
                                <option>3</option>
                                <option>4</option>
                                <option>5</option>
                            </select>
                        </div>
                        <div className="w-1/6">
                            ₹ {quanity * productData.price}
                        </div>
                        <div>
                            <button
                                className="bg-blue-500 hover:bg-blue-700 text-white py-1 px-2 rounded-xl flex items-center my-4"
                                onClick={removeProduct}
                            >
                                Remove
                            </button>
                        </div>
                    </div>
                </>
            )}
        </>
    );
};

const Cart = () => {
    const { setNotifyMessage } = useContext(NotificationBannerContext);
    const [cartData, setCartData] = useState([]);
    const [totalAmount, setTotalAmount] = useState(0);
    const [order, setOrder] = useState({});
    const history = useHistory();

    useEffect(() => {
        const cart = JSON.parse(localStorage.getItem("cart"));
        if (cart) {
            const uCart = cart.filter(function (item, pos, self) {
                return self.indexOf(item) == pos;
            });
            setCartData(uCart);
        }
    }, [totalAmount]);

    const placeOrder = () => {
        localStorage.setItem("order", JSON.stringify(order));
        localStorage.removeItem("cart");
        setCartData([]);
        setNotifyMessage("Order Placed", "success", 1000);
        setOrder({});
        history.push("/order-confirmed");
    };

    return (
        <div className="flex flex-col  justify-between">
            <div className="flex flex-col">
                <div>
                    <h1 className="text-2xl font-bold text-left my-8 ml-8">
                        Your Cart
                    </h1>
                </div>
                {cartData.length !== 0 ? (
                    <div className="flex flex-col items-center">
                        <div className="product-info m-2 p-2 text-lg items-center w-2/3 font-bold flex h-12 bg-gray-100">
                            <div className="w-3/6">Product</div>
                            <div className="w-1/6">Price</div>
                            <div className="w-1/6">Quantity</div>
                            <div className="w-1/4">Total</div>
                        </div>
                        {cartData &&
                            cartData.map((item) => (
                                <ProductInfo
                                    setOrder={setOrder}
                                    setTotalAmount={setTotalAmount}
                                    id={item}
                                />
                            ))}
                        <div className="w-2/3 items-end my-4 text-lg flex justify-end">
                            Total Payable Amount -{" "}
                            <div className="font-bold  text-2xl">
                                {" "}
                                ₹{totalAmount}
                            </div>
                        </div>
                    </div>
                ) : (
                    <div className="flex flex-col text-2xl mt-16 items-center">
                        Please Add Products to Cart
                    </div>
                )}
            </div>
            {cartData.length !== 0 && (
                <div className="flex w-full justify-center relative bottom-0">
                    <div className="w-2/3 text-gray-500 text-sm font-bold my-4">
                        <button
                            onClick={placeOrder}
                            className={`w-full bg-blue-400 hover:bg-green-400 text-black p-2 capitalize text-lg rounded-lg`}
                        >
                            Place Order
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default Cart;
